using System;
using System.Text;
using System.Threading.Tasks;
using Api.Models;
using Api.Services.Database;
using Api.Utils.Extensions;
using Microsoft.AspNetCore.Authorization;
using Microsoft.AspNetCore.Mvc;

namespace Api.Controllers
{
    [Route("api/users")]
    public class UsersController : Controller
    {
        private const string DefaultUserImage =
            "data:image/png;base64,";
        
        private readonly UserService _userService;
        private readonly CodeSnippetService _snippetService;
        private readonly IContainsUserChecker _containsUserChecker;
        public UsersController(IContainsUserChecker containsUserChecker, UserService userService, CodeSnippetService snippetService)
        {
            _containsUserChecker = containsUserChecker;
            _userService = userService;
            _snippetService = snippetService;
        }

        [HttpPost]
        [Route("isAvailable")]
        public IActionResult IsAvailable([FromBody] string name)
        {
            return Json(!_containsUserChecker.ContainsName(name));
        }

        [HttpGet]
        [Authorize]
        [Route("{username}/posted")]
        public async Task<IActionResult> GetPostedSnippets([FromQuery] int page, [FromRoute] string username)
        {
            User user = await _userService.FindByUsername(User.GetUsername());
            if (user is null) return NotFound();

            long[] snippets = await _snippetService.FindSnippetsIdsPostedByUser(user, page);
            return snippets.Length == 0 ? NoContent() : Json(snippets);
        }

        [HttpGet]
        [Authorize]
        [Route("{username}/image")]
        public async Task<IActionResult> GetUserImage([FromRoute] string username)
        {
            User user = await _userService.FindByUsername(username);
            if (user is null) return NotFound();

            if (user.ImageBytes is null)
                return Json(DefaultUserImage);

            string result = "data:image/png;base64," + Convert.ToBase64String(user.ImageBytes);
            return Json(result);
        }
    }
}